package isola;import java.io.IOException;import javax.servlet.Filter;import javax.servlet.FilterChain;import javax.servlet.FilterConfig;import javax.servlet.ServletException;import javax.servlet.ServletRequest;import javax.servlet.ServletResponse;/** * @version 1.0 * @author Umut Gokbayrak */public class CharacterFilter implements Filter {	public void destroy() {	}		/**	 * @param req	 * @param resp	 * @param chain	 * @throws ServletException	 * @throws IOException	 */	public void doFilter(ServletRequest req, ServletResponse resp, FilterChain chain) throws ServletException, IOException {		req.setCharacterEncoding("UTF-8");		/*		 * 		 * if (hasIllegalParams((HttpServletRequest)req)) {		 * 		 * throw new ServletException("Illegal params.");		 * 		 * }		 */		chain.doFilter(req, resp);	}		/**	 * @param config	 * @throws ServletException	 */	public void init(FilterConfig config) throws ServletException {	}}