package isola.model.core;public class AjaxFormResult extends JsObject {	private static final long serialVersionUID = -3588631822272961931L;		public AjaxFormResult() {		super();	}		public void setSuccess(boolean result) {		put("success", result);	}		public void setFailureType(String type) {		put("failureType", type);	}		public void responseText(String text) {		put("responseText", text);	}		public void addData(String fieldId, String fieldValue) {		JsObject data = (JsObject) get("data");		if (data == null) {			data = new JsObject();		}		data.put(fieldId, fieldValue);		put("data", data);	}		public void addError(String fieldId, String fieldValue) {		JsObject error = (JsObject) get("errors");		if (error == null) {			error = new JsObject();		}		error.put(fieldId, fieldValue);		put("errors", error);	}}