package isola.model.core;/** *  * A simple model class holding a label and a value *  * @author Umut Gokbayrak */public class LabelValuePair {	private String label;	private String value;		public LabelValuePair() {		super();	}		public LabelValuePair(String label, String value) {		super();		this.label = label;		this.value = value;	}		public String getLabel() {		return label;	}		public void setLabel(String label) {		this.label = label;	}		public String getValue() {		return value;	}		public void setValue(String value) {		this.value = value;	}}