package isola.model.core;/** * A simple model class holding a label and a value * @author Umut Gokbayrak */public class NameValuePair {	private String name;	private String value;		public NameValuePair() {		super();	}		public NameValuePair(String name, String value) {		super();		this.name = name;		this.value = value;	}		public String getValue() {		return value;	}		public void setValue(String value) {		this.value = value;	}		public String getName() {		return name;	}		public void setName(String name) {		this.name = name;	}}