package isola.model.core;/** * @author Umut Gokbayrak */public class TreeNodeItem {	private String text;	private String id;	private String iconCls;	private boolean leaf = false;	private boolean expanded = false;		public TreeNodeItem() {		super();	}		public TreeNodeItem(String text, String id, String iconCls, boolean leaf, boolean expanded) {		this.text = text;		this.id = id;		this.iconCls = iconCls;		this.leaf = leaf;		this.expanded = expanded;	}		public String getText() {		return text;	}		public void setText(String text) {		this.text = text;	}		public String getId() {		return id;	}		public void setId(String id) {		this.id = id;	}		public String getIconCls() {		return iconCls;	}		public void setIconCls(String cls) {		this.iconCls = cls;	}		public boolean isLeaf() {		return leaf;	}		public void setLeaf(boolean leaf) {		this.leaf = leaf;	}		public boolean isExpanded() {		return expanded;	}		public void setExpanded(boolean expanded) {		this.expanded = expanded;	}}