package isola.model.ext.data;import isola.helpers.JsHelper;import isola.model.core.JsObject;/** *  * <p> * Abstract base class for reading structured data from a data source and * converting it *  * into an object containing Ext.data.Record objects and metadata for use by an * Ext.data.Store. *  * This class is intended to be extended and should not be created directly. For * existing *  * implementations, see Ext.data.ArrayReader, Ext.data.JsonReader and * Ext.data.XmlReader. * </p> *  *  *  * @author Umut Gokbayrak */public class DataReader extends JsObject {	private static final long serialVersionUID = 7037315004335288299L;	private static final String TAG = "Ext.data.DataReader";	/**	 * 	 * This DataReader's configured metadata as passed to the constructor.	 */	public Object meta;		@Override	protected void _postProcessToString() {		super._postProcessToString();		if (meta != null)			put("meta", meta);	}		@Override	public String toString() {		_postProcessToString();		return "new " + TAG + "(" + JsHelper.printJsObject(this) + ")";	}}