package isola.model.ext.data;import isola.helpers.JsHelper;/** *  * An implementation of Ext.data.DataProxy that simply passes the data specified * in its *  * constructor to the Reader when its load method is called. *  *  *  * @author Umut Gokbayrak */public class MemoryProxy extends DataProxy {	private static final long serialVersionUID = 31902121117705104L;	private static final String TAG = "Ext.data.MemoryProxy";	private String data;		public MemoryProxy() {		super();	}		public MemoryProxy(String data) {		this.data = data;	}		@Override	protected void _postProcessToString() {		super._postProcessToString();	}		@Override	public String toString() {		_postProcessToString();		if (data == null) {			return "new " + TAG + "(" + JsHelper.printJsObject(this) + ")";		}		return "new " + TAG + "(" + data + ")";	}}