package isola.model.ext.data;import isola.helpers.JsHelper;import isola.model.core.JsArray;/** *  * Small helper class to make creating Stores from Array data easier. *  *  *  * @author Umut Gokbayrak */public class SimpleStore extends Store {	private static final long serialVersionUID = -5299377350898773003L;	private static final String TAG = "Ext.data.SimpleStore";	/**	 * 	 * The multi-dimensional array of data	 */	public JsArray data;	/**	 * 	 * An array of field definition objects, or field name strings.	 */	public JsArray fields;	/**	 * 	 * The array index of the record id. Leave blank to auto generate ids.	 */	public int id = 0;		@Override	protected void _postProcessToString() {		super._postProcessToString();		if (data != null)			put("data", data);		if (fields != null)			put("fields", fields);		if (id != 0)			put("id", id);	}		@Override	public String toString() {		_postProcessToString();		return "new " + TAG + "(" + JsHelper.printJsObject(this) + ")";	}}