package isola.model.ext.data;import isola.helpers.JsHelper;import isola.model.ext.base.Observable;/** *  * Represents a tree data structure and bubbles all the events for its nodes. * The nodes in *  * the tree have most standard DOM functionality. *  *  *  * @author Umut Gokbayrak */public class Tree extends Observable {	private static final long serialVersionUID = 1399612723516069520L;	private static final String TAG = "Ext.data.Tree";	/**	 * 	 * The token used to separate paths in node ids (defaults to '/').	 */	public String pathSeparator;		@Override	protected void _postProcessToString() {		super._postProcessToString();		if (pathSeparator != null)			put("pathSeparator", pathSeparator);	}		@Override	public String toString() {		_postProcessToString();		return "new " + TAG + "(" + JsHelper.printJsObject(this) + ")";	}}