package isola.model.ext.grid;import isola.helpers.JsHelper;import isola.model.ext.base.Observable;/** *  * A custom wrapper for the Ext.grid.PropertyGrid's Ext.data.Store. This class * handles *  * the mapping between the custom data source objects supported by the grid and * the *  * Ext.grid.PropertyRecord format required for compatibility with the underlying * store. *  * Generally this class should not need to be used directly -- the grid's data * should be *  * accessed from the underlying store via the store property. *  *  *  * @author Umut Gokbayrak */public class PropertyStore extends Observable {	private static final long serialVersionUID = -4178398894960230934L;	private static final String TAG = "Ext.grid.PropertyStore";		public PropertyStore() {		super();	}		@Override	protected void _postProcessToString() {		super._postProcessToString();	}		@Override	public String toString() {		_postProcessToString();		return "new " + TAG + "(" + JsHelper.printJsObject(this) + ")";	}}