package isola.model.ext.layout;import isola.helpers.JsHelper;/** *  * Inherits the anchoring of Ext.layout.AnchorLayout and adds the ability for * x/y *  * positioning using the standard x and y component config options. *  *  *  * @author Umut Gokbayrak */public class AbsoluteLayout extends AnchorLayout {	private static final long serialVersionUID = 6791672096445484581L;	private static final String TAG = "Ext.layout.AbsoluteLayout";	/**	 * 	 * An optional extra CSS class that will be added to the container (defaults	 * to ''). This can	 * 	 * be useful for adding customized styles to the container or any of its	 * children using	 * 	 * standard CSS rules.	 */	public String extraCls;	/**	 * 	 * True to hide each contained item on render (defaults to false).	 */	public boolean renderHidden = false;		public AbsoluteLayout() {		super();	}		@Override	protected void _postProcessToString() {		super._postProcessToString();		if (extraCls != null)			put("extraCls", extraCls);		if (renderHidden)			put("renderHidden", renderHidden);	}		@Override	public String toString() {		_postProcessToString();		return "new " + TAG + "(" + JsHelper.printJsObject(this) + ")";	}}