package isola.model.ext.menu;import isola.helpers.JsHelper;/** *  * A base utility class that adapts a non-menu component so that it can be * wrapped by a menu item *  * and added to a menu. It provides basic rendering, activation management and * enable/disable logic *  * required to work in menus. *  *  *  * @author Umut Gokbayrak */public class Adapter extends BaseItem {	private static final long serialVersionUID = -268186287130369292L;	private static final String TAG = "Ext.menu.Adapter";		public Adapter() {		super();	}		@Override	protected void _postProcessToString() {		super._postProcessToString();	}		@Override	public String toString() {		_postProcessToString();		return "new " + TAG + "(" + JsHelper.printJsObject(this) + ")";	}}