package isola.model.ext.menu;import isola.helpers.JsHelper;/** *  * A menu item that wraps the Ext.ColorPalette component. *  *  *  * @author Umut Gokbayrak */public class ColorItem extends Adapter {	private static final long serialVersionUID = -8341639926386163548L;	private static final String TAG = "Ext.menu.ColorItem";		public ColorItem() {		super();	}		@Override	protected void _postProcessToString() {		super._postProcessToString();	}		@Override	public String toString() {		_postProcessToString();		return "new " + TAG + "(" + JsHelper.printJsObject(this) + ")";	}}