package isola.model.ext.menu;import isola.helpers.JsHelper;/** *  * A menu containing a Ext.menu.ColorItem component (which provides a basic * color picker). *  *  *  * @author Umut Gokbayrak */public class ColorMenu extends Menu {	private static final long serialVersionUID = 6128421623047043316L;	private static final String TAG = "Ext.menu.ColorMenu";		public ColorMenu() {		super();	}		@Override	protected void _postProcessToString() {		super._postProcessToString();	}		@Override	public String toString() {		_postProcessToString();		return "new " + TAG + "(" + JsHelper.printJsObject(this) + ")";	}}