package isola.model.ext.menu;import isola.helpers.JsHelper;/** *  * Adds a separator bar to a menu, used to divide logical groups of menu items. * Generally you *  * will add one of these by using "-" in you call to add() or in your items * config rather than *  * creating one directly. *  *  *  * @author Umut Gokbayrak */public class Separator extends BaseItem {	private static final long serialVersionUID = -5709096016136643631L;	private static final String TAG = "Ext.menu.Separator";	/**	 * 	 * True to hide the containing menu after this item is clicked (defaults to	 * false)	 */	public boolean hideOnClick = false;	/**	 * 	 * The default CSS class to use for separators (defaults to "x-menu-sep")	 */	public String itemCls;		public Separator() {		super();	}		@Override	protected void _postProcessToString() {		super._postProcessToString();		if (hideOnClick)			put("hideOnClick", hideOnClick);		if (itemCls != null)			put("itemCls", itemCls);	}		@Override	public String toString() {		_postProcessToString();		return "new " + TAG + "(" + JsHelper.printJsObject(this) + ")";	}}