package isola.model.ext.menu;import isola.helpers.JsHelper;/** *  * Adds a static text string to a menu, usually used as either a heading or * group separator. *  *  *  * @author Umut Gokbayrak */public class TextItem extends BaseItem {	private static final long serialVersionUID = 112128800451210558L;	private static final String TAG = "Ext.menu.TextItem";	/**	 * 	 * True to hide the containing menu after this item is clicked (defaults to	 * false)	 */	public boolean hideOnClick = false;	/**	 * 	 * The default CSS class to use for text items (defaults to "x-menu-text")	 */	public String itemCls;	/**	 * 	 * The text to display for this item (defaults to '')	 */	public String text;	private boolean printWithNoTags = false;		public TextItem() {		super();	}		@Override	protected void _postProcessToString() {		super._postProcessToString();		if (hideOnClick)			put("hideOnClick", hideOnClick);		if (itemCls != null)			put("itemCls", itemCls);		if (text != null)			put("text", text);	}		@Override	public String toString() {		_postProcessToString();		if (printWithNoTags) {			return JsHelper.printJsObject(this);		}		return "new " + TAG + "(" + JsHelper.printJsObject(this) + ")";	}		public boolean isPrintWithNoTags() {		return printWithNoTags;	}		public void setPrintWithNoTags(boolean printWithNoTags) {		this.printWithNoTags = printWithNoTags;	}}