package isola.model.ext.toolbar;import isola.helpers.JsHelper;import isola.model.ext.constants.XTypes;import isola.model.ext.form.Button;/** *  * A button that renders into a toolbar. Use the handler config to specify *  * a callback function to handle the button's click event. *  *  *  * <pre> * &lt;code&gt; * 
 * new Ext.Panel({ * 
 * 	tbar : [ * 
 * 	{text: 'OK', handler: okHandler} // tbbutton is the default xtype if not specified * 
 * ] * 
 * }); * 
 * &lt;/code&gt; * </pre> *  *  *  * @author Umut Gokbayrak */public class ToolbarButton extends Button {	private static final long serialVersionUID = -1433521401935333352L;	private static final String TAG = "Ext.Toolbar.Button";	/**	 * 	 * <p>	 * The registered xtype to create. This config option is not used when	 * passing a	 * 	 * config object into a constructor. This config option is used only when	 * lazy	 * 	 * instantiation is being used, and a child item of a Container is being	 * 	 * specified not as a fully instantiated Component, but as a Component	 * config	 * 	 * object. The xtype will be looked up at render time up to determine what	 * 	 * type of child Component to create.	 * </p>	 * 	 * 	 * 	 * @see XTypes <p>	 *      If you subclass Components to create your own Components, you may	 *      register	 * 	 *      them using Ext.ComponentMgr.registerType in order to be able to take	 *      advantage	 * 	 *      of lazy instantiation and rendering.	 *      </p>	 */	public String xtype = XTypes.tbbutton;		public ToolbarButton() {		super();	}		@Override	protected void _postProcessToString() {		super._postProcessToString();		if (xtype != null)			put("xtype", xtype);	}		@Override	public String toString() {		_postProcessToString();		return "new " + TAG + "(" + JsHelper.printJsObject(this) + ")";	}}