package isola.model.ext.toolbar;import isola.helpers.JsHelper;import isola.model.ext.constants.XTypes;/** *  * <p> * A simple class that renders text directly into a toolbar. * </p> *  * <pre><code>
 * new Ext.Panel({
 *	tbar : [
 *		{xtype: 'tbtext', text: 'Item 1'} // or simply 'Item 1'
 *	]
 * }); *  *  *  * @author Umut Gokbayrak */public class ToolbarTextItem extends ToolbarItem {	private static final long serialVersionUID = -5077714375171621044L;	private static final String TAG = "Ext.Toolbar.TextItem";	/**	 * 	 * <p>The registered xtype to create. This config option is not used when	 * passing a	 * 	 * config object into a constructor. This config option is used only when	 * lazy	 * 	 * instantiation is being used, and a child item of a Container is being	 * 	 * specified not as a fully instantiated Component, but as a Component	 * config	 * 	 * object. The xtype will be looked up at render time up to determine what	 * 	 * type of child Component to create.</p>	 * 	 * 	 * 	 * @see XTypes <p>If you subclass Components to create your own Components,	 *      you may register	 * 	 *      them using Ext.ComponentMgr.registerType in order to be able to take	 *      advantage	 * 	 *      of lazy instantiation and rendering.</p>	 */	public String xtype = XTypes.tbtext;	/**	 * 	 * Text to display	 */	public String text;	/**	 * 	 * Id for the component	 */	public String id;		public ToolbarTextItem() {		super();	}		@Override	protected void _postProcessToString() {		super._postProcessToString();		if (xtype != null)			put("xtype", xtype);		if (text != null)			put("text", text);		if (id != null)			put("id", id);	}		@Override	public String toString() {		_postProcessToString();		return "new " + TAG + "(" + JsHelper.printJsObject(this) + ")";	}}