package isola.model.ext.tree;import isola.helpers.JsHelper;/** *  * @author Umut Gokbayrak */public class AsyncTreeNode extends TreeNode {	private static final long serialVersionUID = -981051454557543782L;	private static final String TAG = "Ext.tree.AsyncTreeNode";	/**	 * 	 * A TreeLoader to be used by this node (defaults to the loader defined on	 * the tree)	 */	public TreeLoader loader;		public AsyncTreeNode() {		super();	}		@Override	protected void _postProcessToString() {		super._postProcessToString();		if (loader != null)			put("loader", loader);	}		@Override	public String toString() {		_postProcessToString();		return "new " + TAG + "(" + JsHelper.printJsObject(this) + ")";	}}