package isola.model.ext.window;import isola.helpers.JsHelper;import isola.model.core.JsObject;/** *  * An object that represents a group of Ext.Window instances and provides * z-order management and *  * window activation behavior. *  *  *  * @author Umut Gokbayrak */public class WindowGroup extends JsObject {	private static final long serialVersionUID = -7701218708533948450L;	private static final String TAG = "Ext.WindowGroup";	/**	 * 	 * The starting z-index for windows (defaults to 9000)	 */	public int zseed = 9000;		public WindowGroup() {		super();	}		@Override	protected void _postProcessToString() {		super._postProcessToString();		if (zseed != 9000)			put("zseed", zseed);	}		@Override	public String toString() {		_postProcessToString();		return "new " + TAG + "(" + JsHelper.printJsObject(this) + ")";	}}