package isola.model.html.basic;import isola.helpers.ElementParserHtml;import isola.model.html.core.Container;/** *  *  *  * @author Umut Gokbayrak *  *  */public class Bdo extends Container {	private static final String tag = "bdo";		public Bdo() {		super();		setId(tag + (Math.random() * 1000));	}		public Bdo(String id) {		super(id);	}		@Override	public String toString() {		return ElementParserHtml.getToStringOut(this);	}		public String getTag() {		return tag;	}}