package isola.model.html.basic;import isola.helpers.ElementParserHtml;import isola.model.html.core.Element;/** *  *  *  * @author Umut Gokbayrak *  *  */public class Meta extends Element {	private static final String tag = "meta";	/**	 * 	 * This attribute specifies a property's value. This specification does not	 * list legal values for	 * 	 * this attribute.	 */	private String content;	/**	 * 	 * This attribute may be used in place of the name attribute. HTTP servers	 * 	 * use this attribute to gather information for HTTP response message	 * headers.	 */	private String httpEquiv;	/**	 * 	 * This attribute assigns the control name.	 */	private String name;	/**	 * 	 * This attribute names a scheme to be used to interpret the property's	 * value (see the	 * 	 * section on profiles for details).	 */	private String scheme;		public Meta() {		super();	}		public Meta(String name, String content) {		super();		setName(name);		setContent(content);	}		public String getContent() {		return content;	}		/**	 * 	 * This attribute specifies a property's value. This specification does not	 * list legal values for	 * 	 * this attribute.	 * 	 * 	 * 	 * @param content	 */	public void setContent(String content) {		this.content = content;	}		public String getHttpEquiv() {		return httpEquiv;	}		/**	 * 	 * This attribute may be used in place of the name attribute. HTTP servers	 * 	 * use this attribute to gather information for HTTP response message	 * headers.	 * 	 * 	 * 	 * @param httpEquiv	 */	public void setHttpEquiv(String httpEquiv) {		this.httpEquiv = httpEquiv;	}		public String getName() {		return name;	}		/**	 * 	 * This attribute assigns the control name.	 * 	 * 	 * 	 * @param name	 */	public void setName(String name) {		this.name = name;	}		public String getScheme() {		return scheme;	}		/**	 * 	 * This attribute names a scheme to be used to interpret the property's	 * value (see the	 * 	 * section on profiles for details).	 * 	 * 	 * 	 * @param scheme	 */	public void setScheme(String scheme) {		this.scheme = scheme;	}		@Override	public String toString() {		return ElementParserHtml.getToStringOut(this);	}		public String getTag() {		return tag;	}}