package isola.model.html.basic;import isola.helpers.ElementParserHtml;import isola.model.html.core.ContainerNoEvent;import isola.model.html.core.TextNode;/** *  *  *  * @author Umut Gokbayrak *  *  */public class Title extends ContainerNoEvent {	private static final String tag = "title";		/**	 * 	 * Defautl constructor	 */	public Title() {		super();	}		/**	 * 	 * Default constructor which automatically assigns the text node	 * 	 * @param value	 */	public Title(String value) {		addChild(new TextNode(value));	}		@Override	public String toString() {		return ElementParserHtml.getToStringOut(this);	}		public String getTag() {		return tag;	}}