package isola.model.html.block;import isola.helpers.ElementParserHtml;import isola.model.html.core.Container;/** *  *  *  * @author Umut Gokbayrak *  *  */public class Body extends Container {	private static final String tag = "body";	/**	 * 	 * @deprecated This attribute sets the color of text marking hypertext links	 *             when selected	 * 	 *             by the user (for visual browsers).	 */	private String alink;	/**	 * 	 * @deprecated This attribute sets the color of text marking visited	 *             hypertext links (for	 * 	 *             visual browsers).	 */	private String vlink;	/**	 * 	 * @deprecated This attribute sets the color of text marking unvisited	 *             hypertext links	 * 	 *             (for visual browsers)	 */	private String link;	/**	 * 	 * @deprecated This attribute sets the foreground color for text (for visual	 *             browsers).	 */	private String text;	/**	 * 	 * @deprecated The value of this attribute is a URI that designates an image	 *             resource.	 * 	 *             The image generally tiles the background (for visual	 *             browsers).	 */	private String background;	/**	 * 	 * @deprecated This attribute sets the background color for the document	 *             body or table cells.	 * 	 *             This attribute sets the background color of the canvas for	 *             the document body (the BODY element) or for	 * 	 *             tables (the TABLE, TR, TH, and TD elements). Additional	 *             attributes for specifying text color	 * 	 *             can be used with the BODY element.	 */	private String bgColor;	/**	 * 	 * The onload event occurs when the user agent finishes loading a window or	 * all	 * 	 * frames within a FRAMESET. This attribute may be used with BODY and	 * FRAMESET	 * 	 * elements.	 */	private String onLoad;	/**	 * 	 * The onunload event occurs when the user agent removes a document from a	 * window or	 * 	 * frame. This attribute may be used with BODY and FRAMESET elements.	 */	private String onUnload;	/**	 * 	 * The onbeforeunload event occurs before the user agent removes a document	 * from a window or	 * 	 * frame. This attribute may be used with BODY and FRAMESET elements.	 */	private String onBeforeUnload;		public String getAlink() {		return alink;	}		/**	 * 	 * @deprecated This attribute sets the color of text marking hypertext links	 *             when selected	 * 	 *             by the user (for visual browsers).	 * 	 * 	 * 	 * @param alink	 */	public void setAlink(String alink) {		this.alink = alink;	}		public String getVlink() {		return vlink;	}		/**	 * 	 * @deprecated This attribute sets the color of text marking visited	 *             hypertext links (for	 * 	 *             visual browsers).	 * 	 * 	 * 	 * @param vlink	 */	public void setVlink(String vlink) {		this.vlink = vlink;	}		public String getLink() {		return link;	}		/**	 * 	 * @deprecated This attribute sets the color of text marking unvisited	 *             hypertext links	 * 	 *             (for visual browsers)	 * 	 * 	 * 	 * @param link	 */	public void setLink(String link) {		this.link = link;	}		public String getText() {		return text;	}		/**	 * 	 * @deprecated This attribute sets the foreground color for text (for visual	 *             browsers).	 * 	 * 	 * 	 * @param text	 */	public void setText(String text) {		this.text = text;	}		public String getBackground() {		return background;	}		/**	 * 	 * @deprecated The value of this attribute is a URI that designates an image	 *             resource.	 * 	 *             The image generally tiles the background (for visual	 *             browsers).	 * 	 * 	 * 	 * @param background	 */	public void setBackground(String background) {		this.background = background;	}		public String getBgColor() {		return bgColor;	}		/**	 * 	 * @deprecated This attribute sets the background color for the document	 *             body or table cells.	 * 	 *             This attribute sets the background color of the canvas for	 *             the document body (the BODY element) or for	 * 	 *             tables (the TABLE, TR, TH, and TD elements). Additional	 *             attributes for specifying text color	 * 	 *             can be used with the BODY element.	 * 	 * 	 * 	 * @param bgColor	 */	public void setBgColor(String bgColor) {		this.bgColor = bgColor;	}		public String getOnLoad() {		return onLoad;	}		/**	 * 	 * The onload event occurs when the user agent finishes loading a window or	 * all	 * 	 * frames within a FRAMESET. This attribute may be used with BODY and	 * FRAMESET	 * 	 * elements.	 * 	 * 	 * 	 * @param onLoad	 */	public void setOnLoad(String onLoad) {		this.onLoad = onLoad;	}		public String getOnUnload() {		return onUnload;	}		/**	 * 	 * The onunload event occurs when the user agent removes a document from a	 * window or	 * 	 * frame. This attribute may be used with BODY and FRAMESET elements.	 * 	 * 	 * 	 * @param onUnload	 */	public void setOnUnload(String onUnload) {		this.onUnload = onUnload;	}		public String getOnBeforeUnload() {		return onBeforeUnload;	}		/**	 * 	 * The onbeforeunload event occurs before the user agent removes a document	 * from a window or	 * 	 * frame. This attribute may be used with BODY and FRAMESET elements.	 * 	 * 	 * 	 * @param onBeforeUnload	 */	public void setOnBeforeUnload(String onBeforeUnload) {		this.onBeforeUnload = onBeforeUnload;	}		@Override	public String toString() {		return ElementParserHtml.getToStringOut(this);	}		public String getTag() {		return tag;	}}