package isola.model.html.block;import isola.helpers.ElementParserHtml;import isola.model.html.core.Container;/** *  * The HEAD element contains information about the current document, such as its * title, keywords *  * that may be useful to search engines, and other data that is not considered * document content. *  * User agents do not generally render elements that appear in the HEAD as * content. They may, *  * however, make information in the HEAD available to users through other * mechanisms. *  *  *  * @author Umut Gokbayrak */public class Head extends Container {	private static final String tag = "head";	/**	 * 	 * This attribute specifies the location of one or more meta data profiles,	 * separated	 * 	 * by white space. For future extensions, user agents should consider the	 * value to be	 * 	 * a list even though this specification only considers the first URI to be	 * significant.	 * 	 * Profiles are discussed below in the section on meta data.	 */	private String profile;		public String getProfile() {		return profile;	}		/**	 * 	 * This attribute specifies the location of one or more meta data profiles,	 * separated	 * 	 * by white space. For future extensions, user agents should consider the	 * value to be	 * 	 * a list even though this specification only considers the first URI to be	 * significant.	 * 	 * Profiles are discussed below in the section on meta data.	 * 	 * 	 * 	 * @param profile	 */	public void setProfile(String profile) {		this.profile = profile;	}		@Override	public String toString() {		return ElementParserHtml.getToStringOut(this);	}		public String getTag() {		return tag;	}}