package isola.model.html.core;import java.util.ArrayList;import java.util.List;public abstract class ContainerNoEvent extends Element implements ElementInt {	/**	 * 	 * Child nodes for this element.	 * 	 * 	 */	private List<Object> children = new ArrayList<Object>();		public ContainerNoEvent() {		super();	}		public ContainerNoEvent(String id) {		super(id);	}		/**	 * 	 * Adds a new child to the node tree.	 * 	 * 	 * 	 * @param element	 */	public void addChild(Object element) {		children.add(element);	}		public List<Object> getChildren() {		return children;	}}