package isola.model.html.core;import isola.constants.DocTypes;import isola.helpers.ElementParserHtml;import isola.helpers.JsGenerator;import isola.model.html.block.Html;public class Document {	private StringBuilder jsOut = new StringBuilder();		public Document() {		super();	}		/**	 * 	 * @see DocTypes	 * 	 * @param docType	 */	public Document(int docType) {		super();		setDocType(docType);	}		public StringBuilder getJsOut() {		return jsOut;	}		public void setJsOut(StringBuilder jsOut) {		this.jsOut = jsOut;	}		/**	 * 	 * HTML Document Type	 * 	 * 	 * 	 * @see DocTypes	 */	private int docType = -1;	/**	 * 	 * Html Node	 * 	 * 	 * 	 * @see Html	 */	private Html htmlBlock;		public int getDocType() {		return docType;	}		/**	 * 	 * HTML Document Type	 * 	 * 	 * 	 * @see DocTypes	 * 	 * @param docType	 */	public void setDocType(int docType) {		this.docType = docType;	}		public Html getHtmlBlock() {		return htmlBlock;	}		/**	 * 	 * Html Node and its children.	 * 	 * 	 * 	 * @see Html	 * 	 * @param htmlBlock	 */	public void setHtmlBlock(Html htmlBlock) {		this.htmlBlock = htmlBlock;	}		@Override	public String toString() {		String str = DocTypes.getDocType(docType);		if (htmlBlock != null) {			str += ElementParserHtml.getToStringOut(htmlBlock);		}		return str;	}		/**	 * 	 * Uses the Javascript history array to go back one page	 * 	 * 	 * 	 * @throws Exception	 */	public void goBack() throws Exception {		jsOut.append(JsGenerator.goBack());	}		/**	 * 	 * Uses the Javascript history array to go forward one page	 * 	 * 	 * 	 * @throws Exception	 */	public void goForward() throws Exception {		jsOut.append(JsGenerator.goForward());	}		/**	 * 	 * Redirects the browser window to another page.	 * 	 * 	 * 	 * @param location	 * 	 * @throws Exception	 */	public void goLocation(String location) throws Exception {		jsOut.append(JsGenerator.goLocation(location));	}		public void reset() {		jsOut = new StringBuilder();	}}