package isola.model.html.core;/** *  *  *  * @author Umut Gokbayrak *  *  */public abstract class Element {	/**	 * 	 * Namespace attribute may be used by most HTML tags.	 */	private String xmlns;	/**	 * 	 * This attribute assigns a name to an element. This name must be unique in	 * a document.	 */	private String id;		public Element() {		super();	}		public Element(String id) {		this.id = id;	}		/*	 * (non-Javadoc)	 * 	 * @see org.claros.isola.html.core.ElementInt#getXmlns()	 */	public String getXmlns() {		return xmlns;	}		/*	 * (non-Javadoc)	 * 	 * @see org.claros.isola.html.core.ElementInt#setXmlns(java.lang.String)	 */	public void setXmlns(String xmlns) {		this.xmlns = xmlns;	}		/*	 * (non-Javadoc)	 * 	 * @see org.claros.isola.html.core.ElementInt#getId()	 */	public String getId() {		if (id == null) {			id = "element" + (Math.random() * 1000);		}		return id;	}		/*	 * (non-Javadoc)	 * 	 * @see org.claros.isola.html.core.ElementInt#setId(java.lang.String)	 */	public void setId(String id) {		this.id = id;	}		/*	 * (non-Javadoc)	 * 	 * @see org.claros.isola.html.core.ElementInt#toString()	 */	public abstract String toString();		/*	 * (non-Javadoc)	 * 	 * @see org.claros.isola.html.core.ElementInt#getTag()	 */	public abstract String getTag();}