package isola.model.html.form;import isola.helpers.ElementParserHtml;import isola.model.html.core.Container;/** *  *  *  * @author Umut Gokbayrak *  *  */public class OptGroup extends Container {	private static final String tag = "optgroup";	/**	 * 	 * When set for a form control, this boolean attribute disables the control	 * for user input.	 */	private String disabled;	/**	 * 	 * This attribute specifies the label for the option group.	 */	private String label;		public OptGroup() {		super();		setId(tag + (Math.random() * 1000));	}		public OptGroup(String id) {		super(id);	}		public String getDisabled() {		return disabled;	}		/**	 * 	 * When set for a form control, this boolean attribute disables the control	 * for user input.	 * 	 * 	 * 	 * @param disabled	 */	public void setDisabled(String disabled) {		this.disabled = disabled;	}		public String getLabel() {		return label;	}		/**	 * 	 * This attribute specifies the label for the option group.	 * 	 * 	 * 	 * @param label	 */	public void setLabel(String label) {		this.label = label;	}		@Override	public String toString() {		return ElementParserHtml.getToStringOut(this);	}		public String getTag() {		return tag;	}}