package isola.model.html.link;import isola.helpers.ElementParserHtml;import isola.model.html.core.Element;/** *  *  *  * @author Umut Gokbayrak *  *  */public class Base extends Element {	private static final String tag = "base";	/**	 * 	 * This attribute specifies the location of a Web resource, thus defining a	 * link between	 * 	 * the current element (the source anchor) and the destination anchor	 * defined by	 * 	 * this attribute.	 */	private String href;	/**	 * 	 * This attribute specifies the name of a frame where a document is to be	 * opened.	 */	private String target;		public Base(String href) {		setHref(href);	}		public String getHref() {		return href;	}		/**	 * 	 * This attribute specifies the location of a Web resource, thus defining a	 * link between	 * 	 * the current element (the source anchor) and the destination anchor	 * defined by	 * 	 * this attribute.	 * 	 * 	 * 	 * @param href	 */	public void setHref(String href) {		this.href = href;	}		public String getTarget() {		return target;	}		/**	 * 	 * This attribute specifies the name of a frame where a document is to be	 * opened.	 * 	 * 	 * 	 * @param target	 */	public void setTarget(String target) {		this.target = target;	}		@Override	public String toString() {		return ElementParserHtml.getToStringOut(this);	}		public String getTag() {		return tag;	}}