package isola.model.html.phrase;import isola.helpers.ElementParserHtml;import isola.model.html.core.Container;/** *  * Indicates an abbreviated form (e.g., WWW, HTTP, URI, Mass., etc.). *  *  *  * @author Umut Gokbayrak */public class Abbr extends Container {	private static final String tag = "abbr";		public Abbr() {		super();		setId(tag + (Math.random() * 1000));	}		public Abbr(String id) {		super(id);	}		@Override	public String toString() {		return ElementParserHtml.getToStringOut(this);	}		public String getTag() {		return tag;	}}