package isola.model.html.phrase;import isola.helpers.ElementParserHtml;import isola.model.html.core.Container;/** *  * Indicates an acronym (e.g., WAC, radar, etc.). *  *  *  * @author Umut Gokbayrak */public class Acronym extends Container {	private static final String tag = "acronym";		public Acronym() {		super();		setId(tag + (Math.random() * 1000));	}		public Acronym(String id) {		super(id);	}		@Override	public String toString() {		return ElementParserHtml.getToStringOut(this);	}		public String getTag() {		return tag;	}}