package isola.model.html.phrase;import isola.helpers.ElementParserHtml;import isola.model.html.core.Element;/** *  *  *  * @author Umut Gokbayrak *  *  */public class Br extends Element {	private static final String tag = "br";	/**	 * 	 * @deprecated Specifies where the next line should appear in a visual	 *             browser after the line break	 * 	 *             caused by this element. This attribute takes into account	 *             floating objects (images, tables, etc.).	 * 	 *             Possible values:	 * 	 * 	 * 	 *             <ul>	 * 	 *             <li>none: The next line will begin normally. This is the	 *             default value.</li>	 * 	 *             <li>left: The next line will begin at nearest line below any	 *             floating objects on the	 * 	 *             left-hand margin.</li>	 * 	 *             <li>right: The next line will begin at nearest line below any	 *             floating objects on the	 * 	 *             right-hand margin.</li>	 * 	 *             <li>all: The next line will begin at nearest line below any	 *             floating objects on	 * 	 *             either margin.</li>	 */	private String clear;		public Br() {		super();	}		public Br(String id) {		super(id);	}		public String getTag() {		return tag;	}		public String getClear() {		return clear;	}		/**	 * 	 * @deprecated Specifies where the next line should appear in a visual	 *             browser after the line break	 * 	 *             caused by this element. This attribute takes into account	 *             floating objects (images, tables, etc.).	 * 	 *             Possible values:	 * 	 * 	 * 	 *             <ul>	 * 	 *             <li>none: The next line will begin normally. This is the	 *             default value.</li>	 * 	 *             <li>left: The next line will begin at nearest line below any	 *             floating objects on the	 * 	 *             left-hand margin.</li>	 * 	 *             <li>right: The next line will begin at nearest line below any	 *             floating objects on the	 * 	 *             right-hand margin.</li>	 * 	 *             <li>all: The next line will begin at nearest line below any	 *             floating objects on	 * 	 *             either margin.</li>	 * 	 * 	 * 	 * @param clear	 */	public void setClear(String clear) {		this.clear = clear;	}		@Override	public String toString() {		return ElementParserHtml.getToStringOut(this);	}}