package isola.model.html.phrase;import isola.helpers.ElementParserHtml;import isola.model.html.core.Container;/** *  * Contains a citation or a reference to other sources. *  *  *  * @author Umut Gokbayrak */public class Cite extends Container {	private static final String tag = "cite";		public Cite() {		super();		setId(tag + (Math.random() * 1000));	}		public Cite(String id) {		super(id);	}		@Override	public String toString() {		return ElementParserHtml.getToStringOut(this);	}		public String getTag() {		return tag;	}}