package isola.model.html.phrase;import isola.helpers.ElementParserHtml;import isola.model.html.core.Container;/** *  * Designates a fragment of computer code. *  *  *  * @author Umut Gokbayrak */public class Code extends Container {	private static final String tag = "code";		public Code() {		super();		setId(tag + (Math.random() * 1000));	}		public Code(String id) {		super(id);	}		@Override	public String toString() {		return ElementParserHtml.getToStringOut(this);	}		public String getTag() {		return tag;	}}