package isola.model.html.phrase;import isola.helpers.ElementParserHtml;import isola.model.html.core.Container;/** *  * Indicates that this is the defining instance of the enclosed term. *  *  *  * @author Umut Gokbayrak */public class Dfn extends Container {	private static final String tag = "dfn";		public Dfn() {		super();		setId(tag + (Math.random() * 1000));	}		public Dfn(String id) {		super(id);	}		@Override	public String toString() {		return ElementParserHtml.getToStringOut(this);	}		public String getTag() {		return tag;	}}