package isola.model.html.phrase;import isola.helpers.ElementParserHtml;import isola.model.html.core.Container;/** *  *  *  * @author Umut Gokbayrak *  *  */public class Pre extends Container {	private static final String tag = "pre";		public Pre() {		super();		setId(tag + (Math.random() * 1000));	}		public Pre(String id) {		super(id);	}		/**	 * 	 * @deprecated This attribute provides a hint to visual user agents about	 *             the desired	 * 	 *             width of the formatted block. The user agent can use this	 *             information to	 * 	 *             select an appropriate font size or to indent the content	 *             appropriately.	 * 	 *             The desired width is expressed in number of characters. This	 *             attribute	 * 	 *             is not widely supported currently.	 */	private String width;		public String getTag() {		return tag;	}		public String getWidth() {		return width;	}		/**	 * 	 * @deprecated This attribute provides a hint to visual user agents about	 *             the desired	 * 	 *             width of the formatted block. The user agent can use this	 *             information to	 * 	 *             select an appropriate font size or to indent the content	 *             appropriately.	 * 	 *             The desired width is expressed in number of characters. This	 *             attribute	 * 	 *             is not widely supported currently.	 * 	 * 	 * 	 * @param width	 */	public void setWidth(String width) {		this.width = width;	}		@Override	public String toString() {		return ElementParserHtml.getToStringOut(this);	}}