package isola.model.html.phrase;import isola.helpers.ElementParserHtml;import isola.model.html.core.Container;/** *  * Designates sample output from programs, scripts, etc. *  *  *  * @author Umut Gokbayrak */public class Samp extends Container {	private static final String tag = "samp";		public Samp() {		super();		setId(tag + (Math.random() * 1000));	}		public Samp(String id) {		super(id);	}		@Override	public String toString() {		return ElementParserHtml.getToStringOut(this);	}		public String getTag() {		return tag;	}}