package isola.model.html.phrase;import isola.helpers.ElementParserHtml;import isola.model.html.core.Container;/** *  * Indicates an instance of a variable or program argument. *  *  *  * @author Umut Gokbayrak */public class Var extends Container {	private static final String tag = "var";		public Var() {		super();		setId(tag + (Math.random() * 1000));	}		public Var(String id) {		super(id);	}		@Override	public String toString() {		return ElementParserHtml.getToStringOut(this);	}		public String getTag() {		return tag;	}}