package isola.model.html.table;import isola.helpers.ElementParserHtml;import isola.model.html.core.Container;/** *  *  *  * @author Umut Gokbayrak *  *  */public class Col extends Container {	private static final String tag = "col";	/**	 * 	 * @deprecated This attribute specifies the position of an IMG, OBJECT, or	 *             APPLET with respect to its context.	 * 	 *             The following values for align concern the object's position	 *             with respect to surrounding text:	 * 	 *             <ul>	 * 	 *             <li>bottom: means that the bottom of the object should be	 *             vertically aligned with	 * 	 *             the current baseline. This is the default value.</li>	 * 	 *             <li>middle: means that the center of the object should be	 *             vertically aligned with	 * 	 *             the current baseline.</li>	 * 	 *             <li>top: means that the top of the object should be	 *             vertically aligned with the	 * 	 *             top of the current text line.</li>	 * 	 *             </ul>	 * 	 * 	 * 	 *             Two other values, left and right, cause the image to float to	 *             the current left or right margin.	 */	private String align;	/**	 * 	 * This attribute, whose value must be an integer > 0, specifies the number	 * of columns	 * 	 * "spanned" by the COL element; the COL element shares its attributes with	 * all the columns	 * 	 * it spans. The default value for this attribute is 1 (i.e., the COL	 * element refers to a	 * 	 * single column). If the span attribute is set to N > 1, the current COL	 * element shares	 * 	 * its attributes with the next N-1 columns.	 */	private String span;	/**	 * 	 * This attribute specifies the vertical position of data within a cell.	 * Possible values:	 * 	 * <ul>	 * 	 * <li>top: Cell data is flush with the top of the cell.</li>	 * 	 * <li>middle: Cell data is centered vertically within the cell. This is the	 * default value.</li>	 * 	 * <li>bottom: Cell data is flush with the bottom of the cell.</li>	 * 	 * <li>baseline: All cells in the same row as a cell whose valign attribute	 * has this	 * 	 * value should have their textual data positioned so that the first text	 * line occurs	 * 	 * on a baseline common to all cells in the row. This constraint does not	 * apply to	 * 	 * subsequent text lines in these cells.</li>	 * 	 * </ul>	 */	private String valign;	/**	 * 	 * This attribute specifies a default width for each column spanned by the	 * current COL	 * 	 * element. It has the same meaning as the width attribute for the COLGROUP	 * element and	 * 	 * overrides it.	 */	private String width;		public Col() {		super();		setId(tag + (Math.random() * 1000));	}		public Col(String id) {		super(id);	}		public String getAlign() {		return align;	}		/**	 * 	 * @deprecated This attribute specifies the position of an IMG, OBJECT, or	 *             APPLET with respect to its context.	 * 	 *             The following values for align concern the object's position	 *             with respect to surrounding text:	 * 	 *             <ul>	 * 	 *             <li>bottom: means that the bottom of the object should be	 *             vertically aligned with	 * 	 *             the current baseline. This is the default value.</li>	 * 	 *             <li>middle: means that the center of the object should be	 *             vertically aligned with	 * 	 *             the current baseline.</li>	 * 	 *             <li>top: means that the top of the object should be	 *             vertically aligned with the	 * 	 *             top of the current text line.</li>	 * 	 *             </ul>	 * 	 * 	 * 	 *             Two other values, left and right, cause the image to float to	 *             the current left or right margin.	 * 	 * 	 * 	 * @param align	 */	public void setAlign(String align) {		this.align = align;	}		public String getSpan() {		return span;	}		/**	 * 	 * This attribute, whose value must be an integer > 0, specifies the number	 * of columns	 * 	 * "spanned" by the COL element; the COL element shares its attributes with	 * all the columns	 * 	 * it spans. The default value for this attribute is 1 (i.e., the COL	 * element refers to a	 * 	 * single column). If the span attribute is set to N > 1, the current COL	 * element shares	 * 	 * its attributes with the next N-1 columns.	 * 	 * 	 * 	 * @param span	 */	public void setSpan(String span) {		this.span = span;	}		public String getValign() {		return valign;	}		/**	 * 	 * This attribute specifies the vertical position of data within a cell.	 * Possible values:	 * 	 * <ul>	 * 	 * <li>top: Cell data is flush with the top of the cell.</li>	 * 	 * <li>middle: Cell data is centered vertically within the cell. This is the	 * default value.</li>	 * 	 * <li>bottom: Cell data is flush with the bottom of the cell.</li>	 * 	 * <li>baseline: All cells in the same row as a cell whose valign attribute	 * has this	 * 	 * value should have their textual data positioned so that the first text	 * line occurs	 * 	 * on a baseline common to all cells in the row. This constraint does not	 * apply to	 * 	 * subsequent text lines in these cells.</li>	 * 	 * </ul>	 * 	 * 	 * 	 * @param align	 */	public void setValign(String align) {		valign = align;	}		public String getWidth() {		return width;	}		/**	 * 	 * This attribute specifies a default width for each column spanned by the	 * current COL	 * 	 * element. It has the same meaning as the width attribute for the COLGROUP	 * element and	 * 	 * overrides it.	 * 	 * 	 * 	 * @param width	 */	public void setWidth(String width) {		this.width = width;	}		@Override	public String toString() {		return ElementParserHtml.getToStringOut(this);	}		public String getTag() {		return tag;	}}