package isola.model.html.table;import isola.helpers.ElementParserHtml;import isola.model.html.core.Container;/** *  *  *  * @author Umut Gokbayrak *  *  */public class Tr extends Container {	private static final String tag = "tr";	/**	 * 	 * @deprecated This attribute specifies the position of an IMG, OBJECT, or	 *             APPLET with respect to its context.	 * 	 *             The following values for align concern the object's position	 *             with respect to surrounding text:	 * 	 *             <ul>	 * 	 *             <li>bottom: means that the bottom of the object should be	 *             vertically aligned with	 * 	 *             the current baseline. This is the default value.</li>	 * 	 *             <li>middle: means that the center of the object should be	 *             vertically aligned with	 * 	 *             the current baseline.</li>	 * 	 *             <li>top: means that the top of the object should be	 *             vertically aligned with the	 * 	 *             top of the current text line.</li>	 * 	 *             </ul>	 * 	 * 	 * 	 *             Two other values, left and right, cause the image to float to	 *             the current left or right margin.	 */	private String align;	/**	 * 	 * @deprecated. This attribute sets the background color for the document	 *              body or table cells.	 * 	 *              This attribute sets the background color of the canvas for	 *              the document body (the BODY element) or for	 * 	 *              tables (the TABLE, TR, TH, and TD elements). Additional	 *              attributes for specifying text color	 * 	 *              can be used with the BODY element.	 */	private String bgColor;	/**	 * 	 * This attribute specifies the vertical position of data within a cell.	 * Possible values:	 * 	 * <ul>	 * 	 * <li>top: Cell data is flush with the top of the cell.</li>	 * 	 * <li>middle: Cell data is centered vertically within the cell. This is the	 * default value.</li>	 * 	 * <li>bottom: Cell data is flush with the bottom of the cell.</li>	 * 	 * <li>baseline: All cells in the same row as a cell whose valign attribute	 * has this	 * 	 * value should have their textual data positioned so that the first text	 * line occurs	 * 	 * on a baseline common to all cells in the row. This constraint does not	 * apply to	 * 	 * subsequent text lines in these cells.</li>	 * 	 * </ul>	 */	private String valign;		public Tr() {		super();		setId(tag + (Math.random() * 1000));	}		public Tr(String id) {		super(id);	}		public String getAlign() {		return align;	}		/**	 * 	 * @deprecated This attribute specifies the position of an IMG, OBJECT, or	 *             APPLET with respect to its context.	 * 	 *             The following values for align concern the object's position	 *             with respect to surrounding text:	 * 	 *             <ul>	 * 	 *             <li>bottom: means that the bottom of the object should be	 *             vertically aligned with	 * 	 *             the current baseline. This is the default value.</li>	 * 	 *             <li>middle: means that the center of the object should be	 *             vertically aligned with	 * 	 *             the current baseline.</li>	 * 	 *             <li>top: means that the top of the object should be	 *             vertically aligned with the	 * 	 *             top of the current text line.</li>	 * 	 *             </ul>	 * 	 * 	 * 	 *             Two other values, left and right, cause the image to float to	 *             the current left or right margin.	 * 	 * 	 * 	 * @param align	 */	public void setAlign(String align) {		this.align = align;	}		public String getBgColor() {		return bgColor;	}		/**	 * 	 * @deprecated. This attribute sets the background color for the document	 *              body or table cells.	 * 	 *              This attribute sets the background color of the canvas for	 *              the document body (the BODY element) or for	 * 	 *              tables (the TABLE, TR, TH, and TD elements). Additional	 *              attributes for specifying text color	 * 	 *              can be used with the BODY element.	 * 	 * 	 * 	 * @param bgColor	 */	public void setBgColor(String bgColor) {		this.bgColor = bgColor;	}		public String getValign() {		return valign;	}		/**	 * 	 * This attribute specifies the vertical position of data within a cell.	 * Possible values:	 * 	 * <ul>	 * 	 * <li>top: Cell data is flush with the top of the cell.</li>	 * 	 * <li>middle: Cell data is centered vertically within the cell. This is the	 * default value.</li>	 * 	 * <li>bottom: Cell data is flush with the bottom of the cell.</li>	 * 	 * <li>baseline: All cells in the same row as a cell whose valign attribute	 * has this	 * 	 * value should have their textual data positioned so that the first text	 * line occurs	 * 	 * on a baseline common to all cells in the row. This constraint does not	 * apply to	 * 	 * subsequent text lines in these cells.</li>	 * 	 * </ul>	 * 	 * 	 * 	 * @param valign	 */	public void setValign(String valign) {		this.valign = valign;	}		@Override	public String toString() {		return ElementParserHtml.getToStringOut(this);	}		public String getTag() {		return tag;	}}