package isola.tags.js.ajax;import javax.servlet.jsp.JspException;import isola.helpers.JsGenerator;import isola.tags.base.BaseTag;/** * Ajax loader. It loads the specified url into the given Html element. * If a loading indicator is wanted to be shown an ajax loader css must be * provided. A sample ajax indicator css is as follows. * <pre> * 	.ajax-loading { * 	padding: 20px 0; * 	background: url(http://demos.mootools.net/demos/Group/spinner.gif) no-repeat center; * } * </pre> * @author Umut Gokbayrak */public class AjaxLoadTag extends BaseTag {	private static final long serialVersionUID = -3442731447528470015L;	private String elementId;	private String url;	private boolean showLoadingProgress = true;	private String ajaxLoaderCss;		@Override	public int doEndTag() throws JspException {		try {			String str = JsGenerator.ajaxloadInto(elementId, url, showLoadingProgress, ajaxLoaderCss);			pageContext.getOut().write(str);		} catch (Exception e) {			throw new JspException(e);		}		return EVAL_PAGE;	}		@Override	protected void clear() {		elementId = null;		url = null;		showLoadingProgress = true;		ajaxLoaderCss = null;	}		/**	 * The element which the content will be loaded into	 */	public void setElementId(String elementId) {		this.elementId = elementId;	}		/**	 * The url of the content which will be loaded into elementId	 */	public void setUrl(String url) {		this.url = url;	}		/**	 * Whilst loading shall we show a loading progress?	 * @param showLoadingProgress	 */	public void setShowLoadingProgress(boolean showLoadingProgress) {		this.showLoadingProgress = showLoadingProgress;	}		/**	 * If we want to show a loading progress please also supply the CSS	 * of the load progress indicator. A sample:	 * <pre>	 * 	.ajax-loading {	 * 	padding: 20px 0;	 * 	background: url(http://demos.mootools.net/demos/Group/spinner.gif) no-repeat center;	 * }	 * </pre>	 * 	 * And we shall be passing the value: "ajax-loading"	 * 	 * @param ajaxLoaderCss	 */	public void setAjaxLoaderCss(String ajaxLoaderCss) {		this.ajaxLoaderCss = ajaxLoaderCss;	}}