package isola.tags.js.dom;import javax.servlet.jsp.JspException;import isola.helpers.JsGenerator;import isola.tags.base.BaseTag;/** *  *  *  * @author Umut Gokbayrak */public class CloneNodeTag extends BaseTag {	private static final long serialVersionUID = 7766355388809933217L;	private String elementId;	private String parentElementId;	private String newElementId;		@Override	public int doEndTag() throws JspException {		try {			if (parentElementId == null) {				parentElementId = "$document.body";			}			String str = JsGenerator.clone(elementId, parentElementId, newElementId, null, null, null);			pageContext.getOut().write(str);		} catch (Exception e) {			e.printStackTrace();		}		return EVAL_PAGE;	}		@Override	protected void clear() {		elementId = parentElementId = newElementId = null;	}		public void setElementId(String elementId) {		this.elementId = elementId;	}		public void setParentElementId(String parentElementId) {		this.parentElementId = parentElementId;	}		public void setNewElementId(String newElementId) {		this.newElementId = newElementId;	}}