package isola.columntree;import isola.model.core.JsArray;public class ColumnTreeNodeItem {	private String task;	private String duration;	private String user;	private String uiProvider;	private String cls;	private String iconCls;	private JsArray children;	private Boolean leaf;	private Boolean expanded;		public ColumnTreeNodeItem() {		super();	}		public ColumnTreeNodeItem(String task, String duration, String user, String uiProvider, String cls, String iconCls, Boolean leaf, Boolean expanded) {		this.task = task;		this.duration = duration;		this.user = user;		this.uiProvider = uiProvider;		this.cls = cls;		this.iconCls = iconCls;		this.leaf = leaf;		this.expanded = expanded;	}		public String getTask() {		return task;	}		public void setTask(String task) {		this.task = task;	}		public String getDuration() {		return duration;	}		public void setDuration(String duration) {		this.duration = duration;	}		public String getUser() {		return user;	}		public void setUser(String user) {		this.user = user;	}		public String getUiProvider() {		return uiProvider;	}		public void setUiProvider(String uiProvider) {		this.uiProvider = uiProvider;	}		public String getCls() {		return cls;	}		public void setCls(String cls) {		this.cls = cls;	}		public String getIconCls() {		return iconCls;	}		public void setIconCls(String iconCls) {		this.iconCls = iconCls;	}		public JsArray getChildren() {		return children;	}		public void setChildren(JsArray children) {		this.children = children;	}		public void setLeaf(Boolean leaf) {		this.leaf = leaf;	}		public void setExpanded(Boolean expanded) {		this.expanded = expanded;	}		public Boolean getLeaf() {		return leaf;	}		public Boolean getExpanded() {		return expanded;	}}