package isola.columntree;import isola.annotations.RemoteMethod;import isola.annotations.RemoteService;import isola.model.core.JsArray;import net.arnx.jsonic.JSON;@RemoteService(uri = "/columntree.js")public class ColumnTreeSampleController {	@RemoteMethod	public String getColumns() {		JsArray root = new JsArray();		ColumnTreeNodeItem row1 = new ColumnTreeNodeItem("ColumnTree Example", "3 Hours", "", "col", "master-task", "task-folder", false, true);		JsArray row1Children = new JsArray();		row1Children.add(new ColumnTreeNodeItem("Abstract Rendering in ColumnTree UI", "15 min", "Umut Gökbayrak", "col", null, "task", true, false));		row1Children.add(new ColumnTreeNodeItem("Create TreeNodeUI with column knowledge", "45 min", "Deneme Bir", "col", null, "task", true, false));		row1Children.add(new ColumnTreeNodeItem("Add CSS to make it look fly", "25 min", "Hebelek was here", "col", null, "task", true, false));		row1Children.add(new ColumnTreeNodeItem("Test and make sure it works", "30 min", "Deneme Üç", "col", null, "task", true, false));		row1.setChildren(row1Children);		root.add(row1);		ColumnTreeNodeItem row2 = new ColumnTreeNodeItem("Custom Field Example", "2.5 Hours", "", "col", "master-task", "task-folder", false, false);		JsArray row2Children = new JsArray();		row2Children.add(new ColumnTreeNodeItem("Implement \"Live Search\" on extjs.com from Alex", "15 min", "Umut Gökbayrak", "col", null, "task", true, false));		row2Children.add(new ColumnTreeNodeItem("Create TreeNodeUI with column knowledge", "45 min", "Deneme Bir", "col", null, "task", true, false));		row2.setChildren(row2Children);		root.add(row2);		String encoded = JSON.encode(root);		return encoded;	}}