package isola.forms;import isola.annotations.RemoteMethod;import isola.annotations.RemoteService;import isola.model.core.AjaxFormResult;import net.arnx.jsonic.JSON;@RemoteService(uri = "/forms.js")public class FormSampleController {	@RemoteMethod	public String loadForm() {		AjaxFormResult res = new AjaxFormResult();		res.setSuccess(true);		res.addData("txt1", "umutgokbayrak");		res.addData("email1", "kkk");		String encode = JSON.encode(res);		return encode;	}		@RemoteMethod	public String loadForm(String username, String password) {		System.out.println("Saving data...");		System.out.println("Username: " + username);		System.out.println("Password: " + password);		AjaxFormResult res = new AjaxFormResult();		res.setSuccess(false);		res.addError("txt1", "Username daha güzel olsun...");		res.addError("email1", "Bu ne bicim password?");		res.addData("username", "umut");		res.addData("password", "hebelek");		String encode = JSON.encode(res);		return encode;	}}