package isola.samples;public class GuestBookEntry {	private String fromName;	private String subject;	private String entryBody;		public GuestBookEntry() {		super();	}		public GuestBookEntry(String fromName, String subject, String entryBody) {		this.entryBody = entryBody;		this.fromName = fromName;		this.subject = subject;	}		public String getFromName() {		return fromName;	}		public void setFromName(String fromName) {		this.fromName = fromName;	}		public String getSubject() {		return subject;	}		public void setSubject(String subject) {		this.subject = subject;	}		public String getEntryBody() {		return entryBody;	}		public void setEntryBody(String entryBody) {		this.entryBody = entryBody;	}}