package isola.samples;import java.io.IOException;import java.util.ArrayList;import java.util.List;import javax.servlet.ServletException;import javax.servlet.http.HttpServlet;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;public class GuestBookEntryServlet extends HttpServlet {	private static final long serialVersionUID = 4022248157098426443L;	private static List<GuestBookEntry> entries = new ArrayList<GuestBookEntry>();		public void doPost(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {		String from = request.getParameter("entryFrom");		String subject = request.getParameter("entrySubject");		String content = request.getParameter("entryContent");		entries.add(new GuestBookEntry(from, subject, content));		request.setAttribute("entries", entries);		request.getRequestDispatcher("sample2/index.jsp").forward(request, response);	}}