package isola.samples;import isola.model.core.TreeNodeItem;import isola.utility.RandomGen;import java.io.IOException;import java.io.PrintWriter;import java.util.ArrayList;import javax.servlet.ServletException;import javax.servlet.http.HttpServlet;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import net.arnx.jsonic.JSON;public class TreeLoaderServlet extends HttpServlet {	private static final long serialVersionUID = -8622165907278320819L;		/**	 * 	 * The doGet method of the servlet. <br>	 * 	 * 	 * 	 * This method is called when a form has its tag value method equals to get.	 * 	 * 	 * 	 * @param request	 *            the request send by the client to the server	 * 	 * @param response	 *            the response send by the server to the client	 * 	 * @throws ServletException	 *             if an error occurred	 * 	 * @throws IOException	 *             if an error occurred	 */	public void doGet(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {		response.setContentType("text/html");		PrintWriter out = response.getWriter();		ArrayList<TreeNodeItem> lst = new ArrayList<TreeNodeItem>();		for (int i = 0; i < 5; i++) {			String txt = RandomGen.getRandomText(5);			lst.add(new TreeNodeItem(txt, "source\\/" + txt, "folder", true, true));		}		out.print(JSON.encode(lst));	}		/**	 * 	 * The doPost method of the servlet. <br>	 * 	 * 	 * 	 * This method is called when a form has its tag value method equals to	 * post.	 * 	 * 	 * 	 * @param request	 *            the request send by the client to the server	 * 	 * @param response	 *            the response send by the server to the client	 * 	 * @throws ServletException	 *             if an error occurred	 * 	 * @throws IOException	 *             if an error occurred	 */	public void doPost(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {		doGet(request, response);	}}