package isola.samples;import isola.model.core.AjaxFormResult;import java.io.IOException;import javax.servlet.ServletException;import javax.servlet.http.HttpServlet;import javax.servlet.http.HttpServletRequest;import javax.servlet.http.HttpServletResponse;import net.arnx.jsonic.JSON;public class ValidateServlet extends HttpServlet {	private static final long serialVersionUID = -5874978685919845283L;	private static final String USERNAME = "admin";	private static final String PASSWORD = "password";		public void doPost(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException {		String username = request.getParameter("username");		String password = request.getParameter("password");		AjaxFormResult result = new AjaxFormResult();		if (username == null || username.trim().equals("")) {			result.setSuccess(false);			result.addError("username", "Kullanýcý adý boþ olamaz");		} else if (password == null || password.trim().equals("")) {			result.setSuccess(false);			result.addError("password", "Þifre boþ olamaz");		} else if (username.trim().equals(USERNAME) && !password.trim().equals(PASSWORD)) {			result.setSuccess(false);			result.addError("password", "Þifrenizi kontrol ediniz.");		} else if (!username.trim().equals(USERNAME) && password.trim().equals(PASSWORD)) {			result.setSuccess(false);			result.addError("username", "Kullanýcý adýnýzý kontrol ediniz.");		} else if (!username.trim().equals(USERNAME) && !password.trim().equals(PASSWORD)) {			result.setSuccess(false);			result.addError("username", "Kullanýcý adýnýzý kontrol ediniz.");			result.addError("password", "Þifrenizi kontrol ediniz.");		} else {			result.setSuccess(true);			result.addData("url", "home.jsp");		}		String encoded = JSON.encode(result);		response.setContentType("text/html; charset=utf-8");		response.getWriter().write(encoded);	}}